if (!window.customElements) {
  var xhrObj = new XMLHttpRequest();
  // open and send a synchronous request
  xhrObj.open('GET', 'https://unpkg.com/@webcomponents/webcomponentsjs@2.2.7/bundles/webcomponents-ce.js', false);
  xhrObj.send('');
  // execute script syncronously
  eval(xhrObj.responseText);
}
